import 'package:flutter/material.dart';
import 'package:flutter_markdown/flutter_markdown.dart';

enum PolicySection { terms, privacy, community, safety }

class DetailsScreen extends StatelessWidget {
  final PolicySection? initialSection;
  const DetailsScreen({super.key, this.initialSection});

  @override
  Widget build(BuildContext context) {
    final items = <_PolicyItem>[
      _PolicyItem(title: '利用規約', markdown: _termsMd),
      _PolicyItem(title: 'プライバシーポリシー', markdown: _privacyMd),
      _PolicyItem(title: 'コミュニティガイドライン', markdown: _communityMd),
      _PolicyItem(title: '安心・安全のガイドライン', markdown: _safetyMd),
    ];

    int openIndex = -1;
    if (initialSection != null) {
      switch (initialSection!) {
        case PolicySection.terms:     openIndex = 0; break;
        case PolicySection.privacy:   openIndex = 1; break;
        case PolicySection.community: openIndex = 2; break;
        case PolicySection.safety:    openIndex = 3; break;
      }
    }

    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        title: const Text('規約とガイドライン'),
        centerTitle: true,
        backgroundColor: Colors.transparent,
        elevation: 0,
        foregroundColor: Colors.white,
      ),
      body: ListView.builder(
        padding: const EdgeInsets.fromLTRB(12, 8, 12, 24),
        itemCount: items.length,
        itemBuilder: (_, i) => _PolicyCard(
          item: items[i],
          initiallyExpanded: i == openIndex,
        ),

      ),
    );
  }
}

class _PolicyItem {
  final String title;
  final String markdown;
  const _PolicyItem({required this.title, required this.markdown});
}

  class _PolicyCard extends StatelessWidget {
    final _PolicyItem item;
    final bool initiallyExpanded;

    const _PolicyCard({
      required this.item,
      this.initiallyExpanded = false,
    });

    @override
    Widget build(BuildContext context) {
      final theme = Theme.of(context).copyWith(dividerColor: Colors.transparent);

    return Container(
      margin: const EdgeInsets.symmetric(vertical: 8),
      decoration: BoxDecoration(
        color: const Color(0xFF101010),
        borderRadius: BorderRadius.circular(16),
        border: Border.all(color: Colors.white12),
        boxShadow: const [BoxShadow(color: Colors.black54, blurRadius: 12, offset: Offset(0, 8))],
      ),
      child: Stack(
        children: [
          // 左側アクセントライン
          Positioned(
            left: 0, top: 0, bottom: 0,
            child: Container(
              width: 3,
              decoration: const BoxDecoration(
                gradient: LinearGradient(
                  colors: [Color.fromARGB(255, 255, 255, 255), Color.fromARGB(255, 0, 0, 0), Color.fromARGB(255, 255, 255, 255)],
                  begin: Alignment.topCenter, end: Alignment.bottomCenter,
                ),
              ),
            ),
          ),
          Theme(
            data: theme,
            child: ExpansionTile(
              initiallyExpanded: initiallyExpanded,
              tilePadding: const EdgeInsets.symmetric(horizontal: 16, vertical: 12),
              childrenPadding: const EdgeInsets.fromLTRB(16, 0, 16, 16),
              iconColor: Colors.white,
              collapsedIconColor: Colors.white70,
              title: Text(
                item.title,
                style: const TextStyle(color: Colors.white, fontSize: 16, fontWeight: FontWeight.w700),
              ),
              children: [
                // Markdown 本文（選択・コピーも可）
                MarkdownBody(
                  data: item.markdown,
                  selectable: true,
                  styleSheet: MarkdownStyleSheet(
                    p: const TextStyle(color: Colors.white, height: 1.6, fontSize: 14),
                    h1: const TextStyle(color: Colors.white, fontSize: 20, fontWeight: FontWeight.w800),
                    h2: const TextStyle(color: Colors.white, fontSize: 18, fontWeight: FontWeight.w700),
                    h3: const TextStyle(color: Colors.white, fontSize: 16, fontWeight: FontWeight.w700),
                    listBullet: const TextStyle(color: Colors.white),
                    blockquote: const TextStyle(color: Colors.white70, fontStyle: FontStyle.italic),
